mapper.update:
update party set type=#{type}, start_point=#{startPoint}, start_lat=#{startLat}, start_lng=#{startLng}, end_point=#{endPoint}, total_headcnt=#{totalHeadcnt}, start_date=#{startDate}, start_time=#{startTime},
car_number=null, content=null
car_number=#{carNumber}, content=#{content}
where p_id=#{pId}

update party
set is_confirm=true
where p_id=#{pId}

update party
set is_finish=true
where p_id=#{pId}

update party
set current_headcnt=(
    select nowNum from (
        select current_headcnt+1 as nowNum from party where p_id=#{pId}
    ) as a
)
where p_id=#{pId}
